import { Component } from '@angular/core';

@Component({
  selector: 'app-icon-grid-component',
  templateUrl: './icon-grid-component.component.html',
  styleUrls: ['./icon-grid-component.component.scss']
})
export class IconGridComponentComponent {
  icons: string[] = [
    // Array containing all the icon names, and the icons
    '<svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-card-image" viewBox="0 0 16 16"> <path d="M6.002 5.5a1.5 1.5 0 1 1-3 0 1.5 1.5 0 0 1 3 0z"/> <path d="M1.5 2A1.5 1.5 0 0 0 0 3.5v9A1.5 1.5 0 0 0 1.5 14h13a1.5 1.5 0 0 0 1.5-1.5v-9A1.5 1.5 0 0 0 14.5 2h-13zm13 1a.5.5 0 0 1 .5.5v6l-3.775-1.947a.5.5 0 0 0-.577.093l-3.71 3.71-2.66-1.772a.5.5 0 0 0-.63.062L1.002 12v.54A.505.505 0 0 1 1 12.5v-9a.5.5 0 0 1 .5-.5h13z"/> </svg>',
    'account_balance',
    'chrome_reader_mode',
    'touch_app',
    'mail',
    'movie_creation',
    'wb_cloudy',
    'directions',
    'menu',
    'star',
    // ... add more icons
    ];
}
<div class="icon-grid">
  <div class="icon" *ngFor="let icon of icons">
    <i class="material-icons">{{ icon }}</i>
  </div>
</div>



<style>
.icon-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr); /* 4 columns */
  grid-gap: 10px; /* spacing between icons */
}

.icon {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100px; /* adjust the height as needed */
}

.icon i {
  font-size: 30px; /* adjust the font size as needed */
}
</style>
